mapper.insert:
INSERT INTO
trip_attractions (
trip_no, attraction_no,
sequence
) VALUES (
#{tripNo}, #{attractionNo}, #{sequence}
)

mapper.delete:
DELETE FROM
trip_attractions
WHERE
trip_no = #{tripNo}
AND
attraction_no = #{attractionNo}

mapper.update:
UPDATE trip_attractions
SET sequence = #{sequence}
WHERE
trip_no = #{tripNo} AND attraction_no = #{attractionNo}
