mapper.update:
update users
set pwd = #{pwd}, nickname = #{nickname}, email = #{email}
where id = #{id}

update users
set id = null, nickname = null, is_deleted = 1
where id = #{id}

mapper.select:
select count(*)
from users
where id = #{id}

select count(*)
from users
where nickname = #{nickname}

select *
from users

	is_deleted = 0

		and id = #{id}


		and pwd = #{pwd}


		and nickname = #{nickname}


		and email = #{email}
